#!/usr/bin/env python3
import sys


def solve(N: int, h: "List[int]"):
    ans = 0
    
    for i in range(1,max(h)+1):
        t = [1 if h[k] >= i else 0 for k in range(N)]
        # print(t)
        # if(t[0] == 1 and t[1] == 0):
        #     ans += 1
        for j in range(1, len(t)):
            if(t[j-1] == 1 and t[j] == 0):
                ans += 1

        if(t[-1] == 1):
            ans += 1
            
    print(ans)


    return


# Generated by 1.1.3 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    h = [ int(next(tokens)) for _ in range(N) ]  # type: "List[int]"
    solve(N, h)

if __name__ == '__main__':
    main()
